package switch1215;

public class Switch1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char grade = 'A'; //학점
		switch (grade) {
		case 'A':
			System.out.println("A학점");
			break;
		case'B':
			System.out.println("A학점");
			break;
		case'c':
			System.out.println("A학점");
			break;
			default:
				System.out.println("A학점");
				break;
		}
	}

}
